import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import 'firebase/compat/auth';

const config = {
  apiKey: "AIzaSyBruDaIHlZ_49_yIEWYPPF3hY4oPepwGv4",
  authDomain: "crwn-dem.firebaseapp.com",
  projectId: "crwn-dem",
  storageBucket: "crwn-dem.appspot.com",
  messagingSenderId: "292125472552",
  appId: "1:292125472552:web:00bf7b676f99b68330821d",
  measurementId: "G-HBTWMGP2CG"
};

firebase.initializeApp(config);


export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({prompt: 'select-account'});

export const signInWithGoogle = () => auth.signInWithPopup(provider);
export default firebase;
